package com.mylearnings.java.core.method;

/**
 * @author uppus
 *
 */
public class TrignometryCalculation {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		TrignometryCalculation obj=new TrignometryCalculation();
		obj.sine();
		obj.cos();
		obj.tan();
		
	}
	public void sine(){
		System.out.println("value of sin(90) is :"+ Math.sin(90));
	}
	public void cos(){
		System.out.println("value of cos(90) is :"+ Math.cos(90));
	}
	public void tan(){
		System.out.println("value of tan(90) is :"+ Math.tan(90));
	}

}
